import { ArrowLeftOutlined, ArrowRightOutlined } from "@material-ui/icons";
import styled from "styled-components";
import { sliderItems } from "./data";
import React, { useState}
    from "react";
import AddShoppingCartIcon from '@mui/icons-material/AddShoppingCart';


const Container = styled.div`
    background-image: #E8E8E8;
    display: flex;
    height: 100%;
    width: 100%;


    position: relative;

    overflow: hidden;


`

const Arrow = styled.div`
    width: 50px;
    height: 50px;
    align-items: center;
    background-color: black;
    color: white;
    border-radius: 50%;
    display: flex;
    justify-content: center;
    position: absolute;
    top: 0;
    bottom: 0;
    left: ${props=> props.direction === "left" && "10px" };
    right: ${props=> props.direction === "right" && "10px" };
    margin: auto;
    cursor: pointer;
    opacity: 0.5;
    z-index: 2;

`
const Wrapper = styled.div`
    display: flex;
    transition: all 1.2s ease;
    transform: translateX(${props => props.slideIndex * -100}vw);

`
const Slide = styled.div`
    display: flex;
    align-items: center;
    flex: 1;
    width: 100vw;
    height: 100%;



`;

const ImgContainer = styled.div`
    align-items: center;
    border-radius: 10px;

    box-shadow: 2px 2px 10px gray;
    display: flex;
    flex: 1;
    margin: 0px 100px;
    padding: 5px;

`;

const Image = styled.img`
    border-radius: 25px;


    height: 275px;


`;

const InfoContainer = styled.div`


    border-radius: 50px;
    display: flex;
    flex-direction: column;
    flex: 1;




    height: 100px
    justify-content: center;
    align-items: center;

    `;

const Title = styled.h1` 
    font-size: 30px;
    color: black;
    `;

const DescriptionWapper = styled.div`
    display: flex;
    flex-direction: column;
    text-align: center;

`;

const Desc = styled.p`
    color: black;
    font-size: 16px;
    flex: 1;
`;

const Price = styled.div`
    color: darkgreen;
`;

const ButtonWrapper = styled.div`
    flex: 1;
    display: flex;
    flex-direction: column;
    padding-bottom: 20px;
    align-items: center;

`


const Button = styled.button`
    border-radius: 50px;
    background-color: #FFEFEF;
    cursor: pointer;
    width: 75px;
    height: 40px;
    font-size: 12px; 
    padding: 10px;
    margin-bottom: 6px;

`;



const Slider = () => {

    const [slideIndex, setSlideIndex] = useState(0);

    const handleClick = (direction) => {


        if (direction === "left") {
            setSlideIndex(slideIndex > 0 ? slideIndex - 1 : 2 )
        } else {
            setSlideIndex(slideIndex < 2 ? slideIndex + 1 : 0 )
        }
    }

    return (
        <Container>
            <Arrow direction="left" onClick={()=>handleClick("left")} >
                <ArrowLeftOutlined></ArrowLeftOutlined>
            </Arrow>
            <Wrapper slideIndex = {slideIndex}>
                {sliderItems.map(item => (


                <Slide >
                <ImgContainer bg={item.bg}>
                    <Image src={item.img} />

                    <InfoContainer bg={item.descbg} >
                            <Title>{item.title} </Title>
                            <DescriptionWapper >
                                    <Desc>{item.desc} <br></br> {item.condition} </Desc>
                                    <Price>
                                        {item.price}
                                    </Price>
                                <ButtonWrapper >
                                        <Button>
                                            <AddShoppingCartIcon />
                                        </Button>    
                                </ButtonWrapper>

                            </DescriptionWapper>

                    </InfoContainer>
                </ImgContainer>

                <ImgContainer bg={item.bg}>
                    <Image src={item.img2} />

                    <InfoContainer bg={item.descbg} >
                        <Title>{item.title2} </Title>
                        <DescriptionWapper >
                                    <Desc>{item.desc2} <br></br> {item.condition2}</Desc>
                                    <Price>
                                        {item.price2}
                                    </Price>
                            <ButtonWrapper >

                                        <Button>
                                            <AddShoppingCartIcon />
                                        </Button>    
                            </ButtonWrapper>

                        </DescriptionWapper>

                    </InfoContainer>

                </ImgContainer>        

                <ImgContainer bg={item.bg}>
                    <Image src={item.img3} />

                    <InfoContainer bg={item.descbg} >
                            <Title>{item.title3} </Title>
                            <DescriptionWapper >
                                    <Desc>{item.desc3} <br></br> {item.condition3}</Desc>
                                    <Price>
                                        {item.price}
                                    </Price>
                                <ButtonWrapper >

                                    <Button><AddShoppingCartIcon /></Button>    
                                </ButtonWrapper>

                            </DescriptionWapper>

                    </InfoContainer>
                </ImgContainer>

                    </Slide>



                    ))}
            </Wrapper>
            <Arrow direction="right" onClick={()=>handleClick("right")}>
                <ArrowRightOutlined></ArrowRightOutlined>
            </Arrow>
        </Container>
  );
};

export default Slider;
