import React from 'react'
import styled from 'styled-components';

const Container = styled.div`

    align-items: center;
    display: flex;
    justify-content: center;
    padding-top: 80px;
    width: 100%;

`;

const BannerWrapper = styled.div`
    align-items: center;
    background-image: linear-gradient(to bottom right, gray, purple);
    display: flex;
    flex-direction: column;
    height: 300px;
    justify-content: center;
    padding-bottom: 20px;
    width: 100%;
`;

const Title = styled.h1`
    color: white;
    font-size: 40px;
    text-shadow: 2px 2px black;
`;

const Events = styled.h3`
    color: white;
    font-size: 24px;
`;

const Dates = styled.div`
    color: yellow;
    display: inline-block;
`;

const NewsLetterDescription = styled.h4`
    color: white;
    font-size: 22px;
`;

const InputContainer = styled.div`
    display: flex;
    gap: 0;
`;

const NewsLetterInput = styled.input`
    height: 20px;
    width: 300px;
`;

const NewsLetterButton = styled.button`
    height: 20px;
    padding-top: 5px;
`;

const HomeEventBanner = () => {
  return (
    <Container>
          <BannerWrapper>
              <Title>
                  Upcoming Events:
              </Title>
              <Events>
                  Poway Card Show - <Dates>March 14th, 2022</Dates>
              </Events>
              <NewsLetterDescription>
                  Get Updates About Upcoming Events
              </NewsLetterDescription>
              <InputContainer>
                  <NewsLetterInput />
                  <NewsLetterButton />
              </InputContainer>
          </BannerWrapper>      
    </Container>
  )
}

export default HomeEventBanner
