import { Badge } from "@material-ui/core";
import {
  LanguageOutlined,
  Person,
  PersonAdd,
  Search,
  ShoppingCartOutlined,
} from "@material-ui/icons";
import React from "react";
import styled from "styled-components";
import { Link } from "react-router-dom";

const Container = styled.div`
  height: 70px;

  background: #222831;
  display: flex;
  width: 100%;

  justify-content: center;
`;

const Wrapper = styled.div`
  align-items: center;
  display: flex;
  height: 100%;
  justify-content: space-between;
  width: 80%;
`;
const Left = styled.div`
  align-itmes: center;
  display: flex;
  flex: 1;
`;
const Language = styled.span`
  font-size: 18px;
  color: white;
  cursor: pointer;
  padding-top: 8px;
  padding-left: 10px;
  text-shadow: 2px 2px black;
`;

const SearchContainer = styled.div`
  align-itmes: center;
  border-radius: 50px;
  display: flex;
  margin-left: 25px;
  padding: 5px;
  padding-left: 8px;
`;

const Input = styled.input`
  border: 2px solid black;
  border-radius: 20px;
  margin-right: 10px;
  width: 250px;
`;

const Center = styled.div`
  flex: 1;
  text-align: center;
`;
const Logo = styled.h1`
  font-size: 40px;
  color: white;
  letter-spacing: 2px;
`;

const Right = styled.div`
  align-items: center;
  display: flex;
  flex: 1;
  justify-content: flex-end;
`;

const MenuItem = styled.div`
    font-size 25px;
    color: white;
    cursor: pointer;
    padding-left: 15px;
    text-shadow: 2px 2px black;
`;

const Navbar = () => {
  return (
    <Container>
      <Wrapper>
        <Left>
          <LanguageOutlined style={{ paddingTop: "8px", color: "white" }} />
          <Language>EN</Language>
          <SearchContainer>
            <Input></Input>
            <Search
              style={{ color: "white", fontSize: 30, cursor: "pointer" }}
            ></Search>
          </SearchContainer>
        </Left>
        <Center>
          <Logo>
            <Link
              to="/"
              style={{
                textDecoration: "none",
                color: "white",
                textShadow: "2px 2px black",
              }}
            >
              XONSANDSON
            </Link>
          </Logo>
        </Center>
        <Right>
          <MenuItem>
            <Link>
              <Person style={{ paddingTop: "8px", fontSize: 30 }} />{" "}
            </Link>
          </MenuItem>
          <MenuItem>
            {" "}
            <PersonAdd style={{ paddingTop: "8px", fontSize: 30 }} />{" "}
          </MenuItem>
          <MenuItem>
            <Badge badgeContent={4} color="primary">
              <Link to="/Cart" style={{ color: "white" }}>
                <ShoppingCartOutlined />
              </Link>
            </Badge>
          </MenuItem>
        </Right>
      </Wrapper>
    </Container>
  );
};

export default Navbar;
