  font-size: 20px;
  font-weight: 600;
  margin-bottom: 15px;
`;

const Price = styled.span`
  color: darkgreen;
  font-size: 22px;

`;

const CartButton = styled.button`
  background-color: #F05454;
  border: none;
  border-radius: 25px;
  color: white;
  height: 30px;
  margin-bottom: 20px;
  text-shadow: 1px 1px black;
  width 150px;
`;

const CardDescription = styled.div`
  overflow-y: scroll;
  height: 225px;

  width: 600px;

`;

