import React from 'react';
import styled from 'styled-components';
import { CardList } from '../data';


const Container = styled.div`

    border-radius: 10px;
    box-shadow: 2px 2px 10px darkgray;
    height: 250px;
    width: 350px;
    margin: auto;
    margin-top: 100px;

`;

const CardWrapper = styled.div`

    display: flex;
    padding: 15px;
`;

const CardDetailsWrapper = styled.div`
    display: flex;
    gap: 15px;
`;

const CardImg = styled.img`
    height: 220px;
`;

const CardInfoWrapper = styled.div`
    border: solid;
    color: black;
    font-size: 20px;
    width: 100px;
    height: 100px;
`;

const CardCatergory = styled.div`
    color: gray;
`;

const Card = () => {
    return (
        <Container >
            <CardWrapper CardList={CardList} >
                {CardList.map(item => (

                    <CardDetailsWrapper>
                        <CardImg src={item.img} /> 
                        <CardInfoWrapper>
                            <CardCatergory>
                                {item.category}
                            </CardCatergory>

                            {item.title}
                        </CardInfoWrapper>
                    </CardDetailsWrapper>



            ))}
            </CardWrapper>
        </Container>
  );
};

export default Card;
