import React from "react";
import styled from "styled-components";
import Navbar from "../Components/NavBar";
import NavCardMenu from "../Components/NavCardMenu";
import Footer from "../Components/Footer";
import { Add, Remove } from "@material-ui/icons";

const Container = styled.div``;

const CartWrapper = styled.div`
  justify-content: center;
  height: 100vh;
  padding: 20px;
`;

const Title = styled.h1`
  font-weight: 300;
  text-align: center;
`;

const Top = styled.div`
  align-items: center;
  display: flex;
  justify-content: space-between;
  padding: 20px;
`;

const TopTexts = styled.div``;

const TopText = styled.span`
  cursor: pointer;
  text-decoration: underline;
  margin: 0 10px;
`;

const TopButton = styled.button`
  cursor: pointer;
  font-weight: 600;
  padding: 10px;
`;

const Bottom = styled.div`
  display: flex;
  justify-content: space-between;
`;

const Info = styled.div`
  flex: 3;
`;

const Summary = styled.div`
  flex: 1;
`;

const Product = styled.div`
  border-radius: 10px;
  box-shadow: 2px 2px 5px black;
  display: flex;
  height: 300px;
  justify-content: space-between;
  &:hover {
    height: 325px;
  }
`;

const ProductDetail = styled.div`
  display: flex;
  flex: 2;
`;

const ProductImage = styled.img``;

const Details = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: space-around;
  padding: 0 30px;
`;

const ProductName = styled.h1``;

const ProductId = styled.div``;

const ProductSet = styled.div``;

const ProductCondition = styled.div``;

const PriceDetail = styled.div`
  align-items: center;
  display: flex;
  flex: 1;
  flex-direction: column;
  justify-content: center;
`;

const ProductAmountContainer = styled.div`
  align-items: center;
  display: flex;
`;

const ProductAmount = styled.div`
  font-size: 24px;
`;

const ProductPrice = styled.div``;

const Cart = () => {
  return (
    <Container>
      <Navbar />
      <NavCardMenu />
      <CartWrapper>
        <Title>Your Cart</Title>
        <Top>
          <TopButton>CONTINUE SHOPPING</TopButton>
          <TopTexts>
            <TopText>Cart(4)</TopText>
            <TopText>Wishlist(0)</TopText>
          </TopTexts>
          <TopButton>CHECK OUT NOW</TopButton>
        </Top>

        <Bottom>
          <Info>
            <Product>
              <ProductDetail>
                <ProductImage src="https://product-images.tcgplayer.com/fit-in/437x437/106521.jpg" />
                <Details>
                  <ProductName>Gengar</ProductName>
                  <ProductSet>
                    <b>Item Set:</b> Base Set
                  </ProductSet>
                  <ProductCondition>
                    <b>Item Condition:</b> Mint Condition
                  </ProductCondition>
                  <ProductId>
                    <b>ID:</b> 85449754
                  </ProductId>
                </Details>
              </ProductDetail>
              <PriceDetail>
                <ProductAmountContainer>
                  <Add />
                  <ProductAmount>2</ProductAmount>
                  <Remove />
                </ProductAmountContainer>
                <ProductPrice>$40.99</ProductPrice>
              </PriceDetail>
            </Product>
          </Info>
          <Summary>Summary</Summary>
        </Bottom>
      </CartWrapper>
      <Footer />
    </Container>
  );
};

export default Cart;
