import React from 'react'
import styled from 'styled-components'
import Navbar from '../Components/NavBar';
import NavCardMenu from '../Components/NavCardMenu';
import Footer from "../Components/Footer";


const Container = styled.div`

  height: 100vh;
  width; 100%;
`;

const ProductWrapper = styled.div`
  display: flex;
  height: 65.9%;
`;

const ProductImgWrapper = styled.div`
  align-items: center;
  display: flex;
  flex: 2;
  justify-content: center;
`;


const ProductImg = styled.img`
  height: 440px;
`;

const ProductDescriptionWrapper = styled.div`
  border-left: solid;
  flex: 1.5;
`;

const ProductDescription = styled.div`
  display: flex;
  flex-direction: column;

  padding: 60px;
`;

const DescriptionTitle = styled.h1`
  color: #30475E;
  font-size: 50px; 
  text-shadow: 1px 1px black;
`;

const CardSet = styled.div`
  color: gray;
  font-size: 18px;
  font-weight: 600;
  margin-bottom: 20px;

`;

const PSAgrade = styled.div`
  color: gray;
  font-size: 20px;
  font-weight: 600;
  margin-bottom: 20px;
`;

const Condition = styled.div`
  color: blue;
  font-size: 18px;
  font-weight: 600;
  margin-bottom: 20px;
`;

const PriceContainer = styled.div`
  font-size: 20px;
  font-weight: 600;

`;

const Price = styled.span`
  color: darkgreen;
  font-size: 22px;

`;

const Product = () => {
  return (
    <Container>
        <Navbar />
        <NavCardMenu />      
          <ProductWrapper>
            <ProductImgWrapper>
              <ProductImg src='https://product-images.tcgplayer.com/fit-in/437x437/106521.jpg' />
          </ProductImgWrapper>
        <ProductDescriptionWrapper>
          <ProductDescription>
            <DescriptionTitle>Gengar</DescriptionTitle>
            <CardSet>Base Set</CardSet>
            <PSAgrade>PSA: Not Graded</PSAgrade>
            <Condition>Mint Condition</Condition>
            <PriceContainer>
              Price: <Price>$40.99</Price>
            </PriceContainer>
          </ProductDescription>
          </ProductDescriptionWrapper>
          </ProductWrapper>
      <Footer />
    </Container>
  )
}

export default Product
