import { ArrowLeftOutlined, ArrowRightOutlined } from "@material-ui/icons";
import styled from "styled-components";
import { sliderItems } from "./data";
import React, { useState}
from "react";


const Container = styled.div`
    background-image: #E8E8E8;

    display: flex;                                    
    height: 100%;
    width: 100%;
    position: relative;

    overflow: hidden;


`

const Arrow = styled.div`
    width: 50px;
    height: 50px;
    align-items: center;
    background-color: white;
    color: black;
    border-radius: 50%;
    display: flex;
    justify-content: center;
    position: absolute;
    top: 0;
    bottom: 0;
    left: ${props=> props.direction === "left" && "10px" };
    right: ${props=> props.direction === "right" && "10px" };
    margin: auto;
    cursor: pointer;
    opacity: 0.9;
    z-index: 2;

`
const Wrapper = styled.div`

    display: flex;
    transition: all 1.2s ease;
    transform: translateX(${props => props.slideIndex * -100}vw);


`
const Slide = styled.div`
    display: flex;
    align-items: center;
    width: 100vw;
    height: 100%;



`;

const ImgContainer = styled.div`
    align-items: center;
    border-radius: 50px;
    border: solid;
    border-width: 2px;
    box-shadow: 15px 15px 14px black;
    display: flex;
    flex: 1;
    padding-left: 150px;    


`;

const Image = styled.img`
    border-radius: 25px;

    margin-left: 9%;
    height: 350px;


`;

const InfoContainer = styled.div`

    background-image: linear-gradient(to bottom right, black, #9BA4B4) ;
    border-radius: 50px;
    box-shadow: 5px 10px 18px black;
    display: flex;
    flex-direction: column;
    flex: .5;



    width: 350px;
    height: 100px
    justify-content: center;
    align-items: center;    
    `;

const Title = styled.h1` 
    font-size: 80px;
    color: white;
    text-shadow: 2px 2px black;    
    `;

const DescriptionWapper = styled.div`
    display: flex;

`;

const Desc = styled.p`
    color: white;
    margin: 0;
    padding-top: 40px;
    font-size: 25px;
    flex: 4;
`;

const ButtonWrapper = styled.div`
    flex: 1;
    display: flex;
    flex-direction: column;
    padding-bottom: 20px;
    padding-left: 50px;
    margin-top: 60px;

`


const Button = styled.button`
    border-radius: 50px;
    background-color: #FFEFEF;
    cursor: pointer;
    width: 150px;
    height: 40px;
    font-size: 20px; 
    padding: 10px;
    margin-bottom: 6px;

`;



const Slider = () => {

    const [slideIndex, setSlideIndex] = useState(0);

    const handleClick = (direction) => {


        if (direction === "left") {
            setSlideIndex(slideIndex > 0 ? slideIndex - 1 : 2 )
        } else {
            setSlideIndex(slideIndex < 2 ? slideIndex + 1 : 0 )
        }
    }

    return (
        <Container>
            <Arrow direction="left" onClick={()=>handleClick("left")} >
                <ArrowLeftOutlined></ArrowLeftOutlined>
            </Arrow>
            <Wrapper slideIndex = {slideIndex}>
                {sliderItems.map(item => (


                <Slide >
                <ImgContainer bg={item.bg}>
                    <Image src={item.img} />

                    <InfoContainer bg={item.descbg} >
                            <Title>{item.title} </Title>
                            <DescriptionWapper >
                                <Desc>{item.desc} <br></br> {item.condition} <br></br> {item.price} </Desc>
                                <ButtonWrapper >
                                    <Button>Buy Now</Button>
                                    <Button>Add To Cart</Button>    
                                </ButtonWrapper>

                            </DescriptionWapper>

                    </InfoContainer>
                </ImgContainer>

                <ImgContainer bg={item.bg}>
                    <Image src={item.img2} />

                    <InfoContainer bg={item.descbg} >
                        <Title>{item.title2} </Title>
                        <DescriptionWapper >
                            <Desc>{item.desc2} <br></br> {item.condition2} <br></br> {item.price2} </Desc>
                            <ButtonWrapper >
                                <Button>Buy Now</Button>
                                <Button>Add To Cart</Button>    
                            </ButtonWrapper>

                        </DescriptionWapper>

                    </InfoContainer>

                </ImgContainer>        

                <ImgContainer bg={item.bg}>
                    <Image src={item.img3} />

                    <InfoContainer bg={item.descbg} >
                            <Title>{item.title3} </Title>
                            <DescriptionWapper >
                                <Desc>{item.desc3} <br></br> {item.condition3} <br></br> {item.price3} </Desc>
                                <ButtonWrapper >
                                    <Button>Buy Now</Button>
                                    <Button>Add To Cart</Button>    
                                </ButtonWrapper>

                            </DescriptionWapper>

                    </InfoContainer>
                </ImgContainer>

                    </Slide>



                    ))}
            </Wrapper>
            <Arrow direction="right" onClick={()=>handleClick("right")}>
                <ArrowRightOutlined></ArrowRightOutlined>
            </Arrow>
        </Container>
  );
};

export default Slider;
