import React from 'react'
import styled from 'styled-components'
import Navbar from '../Components/NavBar';
import NavCardMenu from '../Components/NavCardMenu';
import Footer from "../Components/Footer";


const Container = styled.div`

  height: 100vh;
  width; 100%;
`;

const ProductWrapper = styled.div`
  display: flex;
  height: 65.9%;
`;

const ProductImgWrapper = styled.div`
  align-items: center;
  display: flex;
  flex: 2;
  justify-content: center;
`;


const ProductImg = styled.img`
  height: 440px;
`;

const ProductDescriptionWrapper = styled.div`
  border-left: solid;
  flex: 1.5;
`;

const ProductDescription = styled.div`
  padding: 60px;
`;

const DescriptionTitle = styled.h1`
  color: #30475E;
  font-size: 50px; 
  text-shadow: 1px 2px black;
`;

const Product = () => {
  return (
    <Container>
        <Navbar />
        <NavCardMenu />      
          <ProductWrapper>
            <ProductImgWrapper>
              <ProductImg src='https://product-images.tcgplayer.com/fit-in/437x437/106521.jpg' />
          </ProductImgWrapper>
        <ProductDescriptionWrapper>
          <ProductDescription>
            <DescriptionTitle>Gengar</DescriptionTitle>
            Lorem ipsum dolor sit amet consectetur, adipisicing elit. Dolore aut exercitationem excepturi. Minima doloribus ut natus harum at itaque a!
          </ProductDescription>
          </ProductDescriptionWrapper>
          </ProductWrapper>
      <Footer />
    </Container>
  )
}

export default Product
