import React from 'react';
import styled from 'styled-components';
import { CardList } from '../data';


const Container = styled.div`

    border-radius: 10px;
    box-shadow: 2px 2px 10px darkgray;
    height: 250px;
    width: 375px;
    margin: auto;
    margin-top: 100px;

`;

const CardWrapper = styled.div`

    display: flex;
    padding: 15px;
`;

const CardDetailsWrapper = styled.div`
    display: flex;
    gap: 15px;
`;

const CardImg = styled.img`
    height: 220px;
`;

const CardInfoWrapper = styled.div`

    width: 100px;
    height: 100px;
`;

const CardCatergory = styled.div`
    color: black;
    font-size: 14px;
    font-weight: bold;
`;

const CardSet = styled.div`
    color: #575757;
    font-size: 12px;

`;

const Title = styled.div`
    font-size: 22px;
    font-weight: bold;
`;

const CardCondition = styled.div`
    color: blue;
`;

const PriceContainer = styled.div`
    display: flex;
    gap: 8px;
`;

const Price = styled.div`
    color: darkgreen;
`;

const Card = () => {
    return (
        <Container >
            <CardWrapper CardList={CardList} >
                {CardList.map(item => (

                    <CardDetailsWrapper>
                        <CardImg src={item.img} /> 
                        <CardInfoWrapper>
                            <CardCatergory>
                                {item.category}
                            </CardCatergory>
                            <CardSet>
                                {item.cardset}
                            </CardSet>
                            <Title>
                                {item.title}
                            </Title>

                            <CardCondition>
                                {item.condition}
                            </CardCondition>

                            <PriceContainer>
                                Price:
                                <Price>
                                {item.price}
                            </Price>
                            </PriceContainer>

                        </CardInfoWrapper>
                    </CardDetailsWrapper>



            ))}
            </CardWrapper>
        </Container>
  );
};

export default Card;
