import React from 'react'
import styled from 'styled-components'

const Container = styled.div`
    align-items: center;
    background-image: linear-gradient(to bottom, #30475E, #222831);
    display: flex;
    height: 250px;
    justify-content: center;
    margin: auto;
    width:  100%;

`;

const FooterWrapper = styled.div`
    display: flex;
    width: 85%;
`;

const Left = styled.div`
    display: flex;
    flex: 1;
    flex-direction: column;
    gap: 10px;
    height: 200px;
h   

`

const LeftTitle = styled.div`
    color: white;
    font-size: 34px;
    font-weight: 800;
    margin-left: 20px;
    text-shadow: 2px 2px black;
`;

const LeftDescription = styled.div`
    margin-left: 20px;
`;

const Center = styled.div`
    display: flex;
    flex: 1;
    flex-direction: column; 
    height: 200px;


    `;

const CenterTitle = styled.div`
    color: white;
    font-size: 26px;
    font-weight: 600;
    margin-left: 20px;
    text-shadow: 2px 2px black;


`;

const CenterDescription = styled.div`

    display: flex;

`;

const CenterLink = styled.li`


`; 

const CenterLeftLinks = styled.ul`
    list-style-type: none;
`


const Right = styled.div`
    display: flex;
    flex: 1;
    flex-direction: column;
    gap: 10px;
    height: 200px;



    `;

const RightTitle = styled.div`
    color: white;
    font-size: 26px;
    font-weight: 600;
    margin-left: 20px;
    text-shadow: 2px 2px black;


`;

const RightDescription = styled.div`
    margin-left: 20px;

`;

const Phone = styled.h3`
    font-size: 22px;
`;

const Email = styled.h3`
    font-size: 22px;
`;

const Footer = () => {
    return (
        <Container>

            <FooterWrapper>

            <Left>
                <LeftTitle>XONSANDSON</LeftTitle>
                <LeftDescription> Lorem ipsum dolor sit amet consectetur adipisicing elit. Quibusdam fuga neque illum laudantium facere. Minima corporis odio quaerat porro. At. </LeftDescription>
            </Left>
            <Center>
                <CenterTitle>Useful Links</CenterTitle>
                <CenterDescription>
                    <CenterLeftLinks>
                        <CenterLink>Our Instagram</CenterLink>
                        <CenterLink>Home</CenterLink>
                        <CenterLink>Contact</CenterLink>

                    </CenterLeftLinks>
                </CenterDescription>
            </Center>
            <Right>
                <RightTitle>Contact Us</RightTitle>
                <RightDescription>
                    <Phone>(619)123-4567</Phone>
                    <Email>xonsandson@email.com</Email>
                </RightDescription>
                </Right>

                </FooterWrapper>
        </Container>
  )
}

export default Footer
